import "./itemPrewiew.css"
function ItemPreview() {
  const [product,setProduct] = useState([])
    useEffect(() => {
      let url = "http://"+ApiUrl+"/product"
      fetch(url)
          .then((response) => response.json())
            .then((data) => {
            //console.log(data)
            setProduct(data[Math.floor(Math.random()*data.length)])
      });
    
  }, []);
  return(
    
    <div class="item">
      <div class ="detail">
        <h1 class ="name">{product.name}</h1>
        <h2>Start from ${product.startingPrice}</h2>
        <h3>{product.description}</h3>
        <button type="button" id="bidButton">BID NOW   &gt;</button>
        </div>
        <img src={`https://res.cloudinary.com/dttoyjaor/image/upload/${product.pictures[0].publicId}`} alt="google plus"></img>
</div>
  );
}

export default ItemPreview;
